import { Component, OnInit, SecurityContext, OnDestroy, ViewChild } from '@angular/core';
import { DomSanitizer, SafeResourceUrl, SafeUrl} from '@angular/platform-browser';
import { ProductsService } from '../services/products.service';
import { Router } from '@angular/router';
import { takeUntil } from 'rxjs/operators';
import { Subject } from 'rxjs';



@Component({
  selector: 'app-checkin',
  templateUrl: './checkin.component.html',
  styleUrls: ['./checkin.component.css']
})
export class CheckinComponent implements OnInit,OnDestroy {
  ngUnsubscribe: Subject<void> = new Subject<void>();
  priceChecked:boolean;
  products: any[];
  unities:any[];
  @ViewChild("table")
  pViewData;

  constructor(public productsService:ProductsService,private router:Router) { 
    this.priceChecked = false;
  }


  ngOnInit(): void {
    this.productsService.chargeProducts();
    this.productsService.setCheckinMode(true);
    this.productsService.cartMode$.pipe(takeUntil(this.ngUnsubscribe)).subscribe(res => {
      if(res){
        this.router.navigate(["/checkout"]);
      }
    });
/*     this.products = [{unity:"ud",quantity:1,name:"Medialuna",price:20,url:"data:image/jpeg;base64,"},
    {unity:"ud",quantity:1,name:"Tortita Negra",price:20,url:"data:image/jpeg;base64,"},
    {unity:"ud",quantity:1,name:"Baguette",price:20,url:"data:image/jpeg;base64,"},
    {unity:"ud",quantity:1,name:"Pan Negro",price:20.5,url:"data:image/jpeg;base64,"}
  ]; */
    this.unities=this.getUnity();
  }

  searchElements(value){
    console.log(value);
  }


  getUnity(){
    return [{label:"1",value:1},{label:"2",value:2},{label:"10",value:10},
    {label:"4",value:4},{label:"15",value:15},{label:"6",value:6}
    ];
  }

  addSelectedProduct(product){
    this.productsService.setOneSelectedProduct(product);
  }    

  ngOnDestroy(): void {
    this.productsService.setCheckinMode(false);
    this.ngUnsubscribe.next();
    this.ngUnsubscribe.unsubscribe();

  }

  
  goToCart(){
    this.productsService.setCartMode(true);
  }
  

}
